import styles from "./ResetPopUp.module.css";

const ResetPopUp = ({
  open,
  onClose,
  newPassword,
  setOpenModal,
  setNewPassword,
}) => {
  if (!open) return null;

  const closePopUp = () => {
    setOpenModal(false);
  };

  const handleResetPassword = async () => {
    try {
      const response = await fetch(
        `http://localhost:3000/resetpassword/${id}`,
        {
          method: "PUT",
          credentials: "include",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ newPassword: btoa(newPassword) }),
        }
      );

      if (response.ok) {
        toast.success("Senha resetada com sucesso!");
        setOpenModal(false); // Fechar o popup após o sucesso
      } else {
        toast.error(
          "Erro ao resetar senha. Verifique os dados e tente novamente."
        );
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div onClick={closePopUp} className={styles.overlay}>
      <div
        onClick={(e) => {
          e.stopPropagation();
        }}
        className={styles.modalContainer}
      >
        <div className={styles.allContainer}>
          <div className={styles.content}>
            <p className={styles.delete}>Digite a sua nova senha: </p>
            <input
              type="password"
              className={styles.inputStyles}
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
            <p className={styles.quest}>Deseja alterar a sua senha atual?</p>
          </div>
          <div className={styles.btnContainer}>
            <button className={styles.btnPrimary} onClick={""}>
              SIM
            </button>
            <button className={styles.btnOutline} onClick={closePopUp}>
              NÃO
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResetPopUp;
