import styles from "./Register.module.css";
import { Link, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import Navbar from "../../components/Navbar/Navbar";
import logoAdd from "../../assets/icons/person_add_black_24dp (1).svg";
import logoReset from "../../assets/icons/baseline-refresh-24px.svg";
import logoSave from "../../assets/icons/baseline-save-24px.svg";

const Register = () => {
  const [usuario, setUsuario] = useState("");
  const [nome, setNome] = useState("");
  const [empresa, setEmpresa] = useState("");
  const [cnpj, setCnpj] = useState("");

  const handleRegister = async () => {
    try {
      const response = await fetch("http://localhost:3000/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          usuario,
          nome,
          empresa,
          cnpj,
        }),
      });

      if (!response.ok) {
        throw new Error("Erro ao registrar usuário");
      }

      console.log("Usuário registrado com sucesso!");
    } catch (error) {
      console.error("Erro ao registrar usuário:", error.message);
    }
  };
  return (
    <div className={styles.container}>
      <Navbar />
      <div className={styles.infoRectangle}>
        <h1 className={styles.titleAdd}>
          <img src={logoAdd} alt="logoAdd" className={styles.logoAdd} />
          CADASTRAR UM NOVO USUÁRIO
        </h1>
        <div className={styles.containerInput}>
          <div>
            <label>USUÁRIO</label>
            <input type="text" className={styles.inputStyles} />
          </div>
          <div>
            <label>NOME</label>
            <input type="text" className={styles.inputStyles} />
          </div>
          <div>
            <label>EMPRESA</label>
            <input
              type="text"
              className={styles.inputStyles}
              onChange={(e) => e.target.value}
            />
          </div>
          <div>
            <label>CNPJ</label>
            <input
              type="text"
              className={styles.inputStyles}
              onChange={(e) => setCnpj(e.target.value)}
            />
          </div>
        </div>
      </div>
      <div className={styles.containerRemove}>
        <div className={styles.quadradoCinza}>
          <img
            className={styles.logoRemove}
            src={logoReset}
            alt="ImagLogoRemove"
          />
        </div>
        <Link className={styles.linkRetangularCinza} to="/register">
          RESETAR SENHA
        </Link>
      </div>
      <div className={styles.containerAdd}>
        <div className={styles.quadradoAzul}>
          <img className={styles.logoCad} src={logoSave} alt="ImagLogoAddem" />
        </div>
        <button className={styles.linkRetangular} onClick={handleRegister}>
          SALVAR ALTERAÇÕES
        </button>
      </div>
    </div>
  );
};

export default Register;
