import logoRemove from "../../assets/icons/delete_black_24dp.svg";
import Logoadd from "../../assets/icons/person_add_black_24dp.svg";
import ListUser from "../../components/ListUser/ListUser";
import Navbar from "../../components/Navbar/Navbar";
import DeletePopUp from "../../components/DeletePopUp/DeletePopUp";
import styles from "./Home.module.css";
import React, { useState } from "react";
import { db } from "../../components/ListUser/db";
import { Link } from "react-router-dom";

const Home = () => {
  const [openModal, setOpenModal] = useState(false);
  const [selectedUsers, setSelectedUsers] = useState([]);

  const toggleUserSelection = (userId) => {
    if (selectedUsers.includes(userId)) {
      setSelectedUsers(selectedUsers.filter((id) => id !== userId));
    } else {
      setSelectedUsers([...selectedUsers, userId]);
    }
  };

  return (
    <div className={styles.container}>
      <Navbar />
      <div className={styles.infoRectangle}>
        <table className={styles.userInfo}>
          <thead>
            <tr>
              <th></th>
              <th>USUÁRIO</th>
              <th>NOME</th>
              <th>EMPRESA</th>
              <th>CNPJ</th>
            </tr>
          </thead>
          <tbody>
            {db.map((user, index) => (
              <ListUser
                key={index}
                user={user}
                onSelectUser={toggleUserSelection}
                isSelected={selectedUsers.includes(user.id)}
              />
            ))}
          </tbody>
        </table>
      </div>

      <div className={styles.containerRemove}>
        <div className={styles.quadradoCinza}>
          <img
            className={styles.logoRemove}
            src={logoRemove}
            alt="ImagLogoRemove"
          />
        </div>
        <button
          onClick={() => {
            setSelectedUsers(db.filter((user) => user).map((user) => user.id));
            setOpenModal(true);
          }}
          className={styles.linkRetangularCinza}
        >
          EXCLUIR USUÁRIO
        </button>
      </div>
      <div className={styles.containerAdd}>
        <div className={styles.quadradoAzul}>
          <img className={styles.logoCad} src={Logoadd} alt="ImagLogoAddem" />
        </div>
        <Link className={styles.linkRetangular} to="/register">
          CADASTRAR NOVO USUÁRIO
        </Link>
      </div>
      <DeletePopUp
        open={openModal}
        onClose={() => {
          setOpenModal(false);
        }}
        selectedUsers={selectedUsers}
      />
    </div>
  );
};

export default Home;
