import Navbar from "../../components/Navbar/Navbar";
import styles from "./Sales.module.css";
import React, { useState } from "react";
import { db } from "../../components/ListSales/dbSales";
import ListSales from "../../components/ListSales/ListSales.js";
import PaymentReceipt from "../../components/PaymentReceipt/PaymentReceipt.js";

const Sales = () => {
  const [openModal, setOpenModal] = useState(false);
  return (
    <div className={styles.container}>
      <Navbar />
      <div className={styles.infoRectangle}>
        <table className={styles.userInfo}>
          <thead>
            <tr>
              <th>ID.VENDAS</th>
              <th>NOME</th>
              <th>CPF</th>
              <th>E-MAIL</th>
              <th>QTD.ITENS</th>
              <th>VALOR</th>
              <th>TROCO</th>
              <th>COMPROVANTE</th>
            </tr>
          </thead>
          <tbody>
            {db.map((sales, index) => (
              <ListSales
                key={index}
                sales={sales}
                openModal={openModal}
                setOpenModal={setOpenModal}
              />
            ))}
          </tbody>
        </table>
      </div>
      <PaymentReceipt
        open={openModal}
        onClose={() => {
          setOpenModal(true);
        }}
      />
    </div>
  );
};

export default Sales;
