import logoRemove from "../../assets/icons/delete_black_24dp.svg";
import Logoadd from "../../assets/icons/person_add_black_24dp.svg";
import ListUser from "../../components/ListUser/ListUser";
import Navbar from "../../components/Navbar/Navbar";
import DeletePopUp from "../../components/DeletePopUp/DeletePopUp";
import styles from "./Home.module.css";
import React, { useState } from "react";
import { db } from "../../components/ListUser/db";
import { Link } from "react-router-dom";

const Home = () => {
  const [selectUser, setSelectUser] = useState([]);
  const [openModal, setOpenModal] = useState(false);

  function handleChecked(e, setIsChecked, user) {
    console.log(e.target.checked);
    if (e.target.checked) {
      setSelectUser([...selectUser, user]);
    } else {
      var posicao = selectUser.findIndex((i) => i.id == user.id);
      if (posicao !== -1) {
        setSelectUser((prevSelectUser) =>
          prevSelectUser
            .slice(0, posicao)
            .concat(prevSelectUser.slice(posicao + 1))
        );
      }
      setIsChecked(false);
    }
    console.log(selectUser);
  }

  return (
    <div className={styles.container}>
      <Navbar />
      <div className={styles.infoRectangle}>
        <table className={styles.userInfo}>
          <thead>
            <tr>
              <th></th>
              <th>USUÁRIO</th>
              <th>NOME</th>
              <th>EMPRESA</th>
              <th>CNPJ</th>
            </tr>
          </thead>
          <tbody>
            {db.map(
              (user, index) => (
                setSelectUser(user),
                (<ListUser key={index} user={user} selectUser={selectUser} />)
              )
            )}
          </tbody>
        </table>
      </div>

      <div className={styles.containerRemove}>
        <div className={styles.quadradoCinza}>
          <img
            className={styles.logoRemove}
            src={logoRemove}
            alt="ImagLogoRemove"
          />
        </div>
        <button
          onClick={() => {
            setOpenModal(true);
          }}
          className={styles.linkRetangularCinza}
        >
          EXCLUIR USUÁRIO
        </button>
      </div>
      <div className={styles.containerAdd}>
        <div className={styles.quadradoAzul}>
          <img className={styles.logoCad} src={Logoadd} alt="ImagLogoAddem" />
        </div>
        <Link className={styles.linkRetangular} to="/register">
          CADASTRAR NOVO USUÁRIO
        </Link>
      </div>
      {db.map((user) => (
        <DeletePopUp
          open={openModal}
          onClose={() => {
            setOpenModal(true);
          }}
          users={user}
        />
      ))}
    </div>
  );
};

export default Home;
