import styles from "./PaymentReceipt.module.css";

const PaymentReceipt = ({ open, onClose, selectSales }) => {
  if (!open) return null;
  console.log(selectSales);
  return (
    <div onClick={onClose} className={styles.overlay}>
      <div
        onClick={(e) => {
          e.stopPropagation();
        }}
        className={styles.modalContainer}
      >
        <div className={styles.allContainer}>
          {
            (console.log(selectSales),
            selectSales &&
              selectSales.map((sale) => (
                <div className={styles.content}>
                  <label>Nome</label>
                  <p>{sale.name}</p>
                  <label>CPF</label>
                  <p>{sale.cpf}</p>
                  <label>E-mail</label>
                  <p>{sale.email}</p>
                </div>
              )))
          }

          <div className={styles.btnContainer}>
            <button className={styles.btnPrimary}>SALVAR</button>
            <button className={styles.btnPrimary} onClick={window.print}>
              IMPRIMIR
            </button>
            <button className={styles.btnOutline}>FECHAR</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentReceipt;
