import styles from "./Login.module.css";
import logo from "../../assets/Group24.svg";
import { Link } from "react-router-dom";
import { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Login = () => {
  const [user, setUser] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async () => {
    try {
      const response = await fetch("http://localhost:3000/login", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-type": "application/json",
        },
        body: JSON.stringify({ user, password }),
      });
      if (user == "" || user == null || password == "" || password == null) {
        toast.error("Preencha os campos para realizar o Login!");
      } else if (response.ok) {
        toast.success("Usuário Logado com sucesso!");
        window.location.href = "/home/user";
      } else if (response.status === 401) {
        toast.error("Sessão expirada! Faça o Login novamente!");
        window.location.href = "/login";
      } else {
        toast.error("Erro ao fazer o login");
      }
    } catch (error) {
      console.error("Error during login: ", error);
    }
  };

  return (
    <div>
      <ToastContainer />
      <div className={styles.containerData}>
        <img className={styles.imgLogo} src={logo} alt="Group 24" />
        <input
          className={styles.inputStyle}
          type="text"
          placeholder="Usuário"
          value={user}
          onChange={(e) => setUser(e.target.value)}
        />
        <input
          className={styles.inputStyle}
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button className={styles.btnLogin} onClick={handleSubmit}>
          LOGIN
        </button>
        <h3>
          <Link className={styles.forget}>Esqueci a senha</Link>
        </h3>
      </div>
    </div>
  );
};

export default Login;
