import styles from "./EditUser.module.css";
import { Link, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import Navbar from "../../components/Navbar/Navbar";
import logoAdd from "../../assets/icons/edit_black_24dp.svg";
import logoReset from "../../assets/icons/baseline-refresh-24px.svg";
import logoSave from "../../assets/icons/baseline-save-24px.svg";

const EditUser = () => {
  const { id } = useParams();
  const [user, setUser] = useState("");
  const [name, setName] = useState("");
  const [company, setCompany] = useState("");
  const [cnpj, setCnpj] = useState("");

  useEffect(async () => {
    try {
      const response = await fetch(`http://localhost:3000/edituser/${id}/`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Origin: `http://localhost:3001/edituser/${id}/`,
        },
      });

      if (response.ok) {
        const userData = await response.json();
        setUser(userData.user);
        setName(userData.name);
        setCompany(userData.company);
        setCnpj(userData.cnpj);
      }
    } catch (error) {
      console.error("Erro ao buscar detalhes do usuário:", error.message);
    }
  }, [id]);

  const handleSubmit = async () => {
    try {
      const response = await fetch(`http://localhost:3000/edituser/${id}/`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ user, name, company, cnpj }),
      });
      if (response.ok) {
        console.log("Alterações salvas com sucesso!");
      } else {
        console.error("Erro ao salvar alterações:", response.statusText);
      }
    } catch (error) {
      console.error("Erro ao editar: " + error);
    }
  };

  return (
    <div className={styles.container}>
      <Navbar />
      <div className={styles.infoRectangle}>
        <h1 className={styles.titleAdd}>
          <img src={logoAdd} alt="logoAdd" className={styles.logoAdd} />
          EDITAR USUÁRIO
        </h1>
        <form className={styles.containerInput}>
          <div>
            <label>USUÁRIO</label>
            <input
              type="text"
              className={styles.inputStyles}
              value={user}
              onChange={(e) => setUser(e.target.value)}
            />
          </div>
          <div>
            <label>NOME</label>
            <input
              type="text"
              className={styles.inputStyles}
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div>
            <label>EMPRESA</label>
            <input
              type="text"
              className={styles.inputStyles}
              value={company}
              onChange={(e) => setCompany(e.target.value)}
            />
          </div>
          <div>
            <label>CNPJ</label>
            <input
              type="text"
              className={styles.inputStyles}
              value={cnpj}
              onChange={(e) => setCnpj(e.target.value)}
            />
          </div>
        </form>
      </div>
      <div className={styles.containerRemove}>
        <div className={styles.quadradoCinza}>
          <img
            className={styles.logoRemove}
            src={logoReset}
            alt="ImagLogoRemove"
          />
        </div>
        <button className={styles.linkRetangularCinza}>RESETAR SENHA</button>
      </div>
      <div className={styles.containerAdd}>
        <div className={styles.quadradoAzul}>
          <img className={styles.logoCad} src={logoSave} alt="ImagLogoAddem" />
        </div>
        <button className={styles.linkRetangular} onClick={handleSubmit}>
          SALVAR ALTERAÇÕES
        </button>
      </div>
    </div>
  );
};

export default EditUser;

