import styles from "./ResetPopUp.module.css";

const ResetPopUp = ({ open, onClose, selectUser, setOpenModal }) => {
  if (!open) return null;

  const closePopUp = () => {
    setOpenModal(false);
  };

  const handleDelete = async () => {
    try {
      const userIds = selectUser.map((user) => user.id);

      const response = await fetch(
        `http://localhost:3000/deleteuser/${userIds}`,

        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      console.log(response);

      if (!response.ok) {
        alert("Não foi possível deletar o(s) usuário(s)");
      } else {
        closePopUp();
        window.location.reload();
        alert("Usuario Deletado com successo!");
      }
    } catch (error) {
      console.error("Erro ao deletar: " + error);
    } finally {
      closePopUp();
    }
  };

  return (
    <div onClick={closePopUp} className={styles.overlay}>
      <div
        onClick={(e) => {
          e.stopPropagation();
        }}
        className={styles.modalContainer}
      >
        <div className={styles.allContainer}>
          <div className={styles.content}>
            <p className={styles.delete}>Digite a sua nova senha: </p>
            <input type="password" className={styles.inputStyles} />
          </div>
          <div className={styles.btnContainer}>
            <p className={styles.quest}>Deseja alterar a sua senha atual?</p>
            <button className={styles.btnPrimary} onClick={handleDelete}>
              SIM
            </button>
            <button className={styles.btnOutline} onClick={closePopUp}>
              NÃO
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResetPopUp;
