import Navbar from "../../components/Navbar/Navbar";
import styles from "./Sales.module.css";
import React, { useEffect, useState } from "react";
import ListSales from "../../components/ListSales/ListSales.js";
import PaymentReceipt from "../../components/PaymentReceipt/PaymentReceipt.js";

const Sales = () => {
  const [selectSales, setSelcetSales] = useState([]);
  const [openModal, setOpenModal] = useState(false);
  const [sales, setSales] = useState([]);
  const [items, setItems] = useState([]);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    const listarVendas = async () => {
      try {
        const response = await fetch("http://localhost:3000/home/sales", {
          method: "POST",
          credentials: "include",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({}),
        });

        if (!response.ok) {
          throw new Error("Erro ao listar o usuário");
        }

        const data = await response.json();
        console.log(data);
        setSales(data.sales);
        setItems(data.items);
      } catch (error) {
        console.error("Erro ao listar o usuário: " + error.mensage);
      }
    };
    listarVendas();
  }, []);

  return (
    <div className={styles.container}>
      {windowWidth <= 480 ? (
        <PaymentReceipt
          open={openModal}
          onClose={() => {
            setOpenModal(true);
          }}
          selectSales={selectSales}
          setOpenModal={setOpenModal}
          items={items}
        />
      ) : (
        ""
      )}
      <Navbar />
      <div className={styles.infoRectangle}>
        <table className={styles.userInfo}>
          <thead>
            <tr>
              <th>ID.VENDAS</th>
              <th>NOME</th>
              <th>CPF</th>
              <th className={styles.noShow}>E-MAIL</th>
              <th className={styles.noShow}>QTD.ITENS</th>
              <th className={styles.noShow}>VALOR</th>
              <th className={styles.noShow}>TROCO</th>
              <th>COMPROVANTE</th>
            </tr>
          </thead>
          <tbody>
            {sales.map((sales, index) => (
              <ListSales
                key={index}
                sales={sales}
                openModal={openModal}
                setOpenModal={setOpenModal}
                selectSales={selectSales}
                setSelectSales={setSelcetSales}
                items={items}
                setItems={setItems}
              />
            ))}
          </tbody>
        </table>
      </div>
      {windowWidth > 480 ? (
        <PaymentReceipt
          open={openModal}
          onClose={() => {
            setOpenModal(true);
          }}
          selectSales={selectSales}
          setOpenModal={setOpenModal}
        />
      ) : (
        ""
      )}
    </div>
  );
};

export default Sales;
