import styles from "./DeletePopUp.module.css";

const DeletePopUp = ({ open, onClose, selectUser }) => {
  if (!open) return null;

  const handleDelete = async () => {
    try {
      const response = await fetch("http://localhost:3000/edituser/:id", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
      });
      if (response.ok) {
        alert("Usuario Deletado com successo!");
      } else {
        alert("Não foi possível deletar o(s) usuário(s)");
      }
    } catch (error) {
      console.error("Erro ao deletar: " + error);
    }
  };

  return (
    <div onClick={onClose} className={styles.overlay}>
      <div
        onClick={(e) => {
          e.stopPropagation();
        }}
        className={styles.modalContainer}
      >
        <div className={styles.allContainer}>
          <div className={styles.content}>
            <p className={styles.delete}>
              Você está prestes a excluir os seguintes usuários:
            </p>
            {selectUser &&
              selectUser.map((user) => (
                <p className={styles.bold}>{user.user}</p>
              ))}
            <p className={styles.quest}>Deseja prosseguir?</p>
          </div>
          <div className={styles.btnContainer}>
            <button className={styles.btnPrimary} onClick={handleDelete}>
              SIM
            </button>
            <button className={styles.btnOutline} onClick={onClose}>
              NÃO
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DeletePopUp;
