import styles from "./EditUser.module.css";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import Navbar from "../../components/Navbar/Navbar";
import logoAdd from "../../assets/icons/edit_black_24dp.svg";
import logoReset from "../../assets/icons/baseline-refresh-24px.svg";
import logoSave from "../../assets/icons/baseline-save-24px.svg";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import ResetPopUp from "../../components/ResetPopUp/ResetPopUp";

const EditUser = () => {
  const { id } = useParams();
  const [openModal, setOpenModal] = useState(false);

  const [userData, setUserData] = useState({
    user: "",
    name: "",
    company: "",
    cnpj: "",
  });

  useEffect(() => {
    fetchUserData();
  }, [id]);

  const fetchUserData = async () => {
    try {
      const response = await fetch(`http://localhost:3000/user/${id}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const userFromApi = await response.json();
        setUserData(userFromApi[0]);
      }
    } catch (error) {
      console.error(error);
    }
  };

  const handleResetPassword = () => {};

  const handleSaveChanges = async () => {
    try {
      const response = await fetch(`http://localhost:3000/edituser/${id}`, {
        method: "PUT",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(userData),
      });

      if (
        userData.user == null ||
        userData.user == "" ||
        userData.name == null ||
        userData.name == "" ||
        userData.company == null ||
        userData.company == "" ||
        userData.cnpj == "" ||
        userData.cnpj == null
      ) {
        toast.error("Preencha os campos para realizar a alteração no usuário!");
      }
      if (response.ok) {
        window.location.href = "/home/user";
        toast.success("Alterações salvas com sucesso!");
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className={styles.container}>
      <ToastContainer />
      <Navbar />
      <div className={styles.infoRectangle}>
        <h1 className={styles.titleAdd}>
          <img src={logoAdd} alt="logoAdd" className={styles.logoAdd} />
          EDITAR USUÁRIO
        </h1>
        <form className={styles.containerInput}>
          <div>
            <label>USUÁRIO</label>
            <input
              type="text"
              className={styles.inputStyles}
              onChange={(e) => {
                setUserData({
                  ...userData,
                  user: e.target.value,
                });
              }}
              value={userData.user}
              name="user"
            />
          </div>
          <div>
            <label>NOME</label>
            <input
              type="text"
              className={styles.inputStyles}
              onChange={(e) => {
                setUserData({
                  ...userData,
                  name: e.target.value,
                });
              }}
              value={userData.name}
              name="name"
            />
          </div>
          <div>
            <label>EMPRESA</label>
            <input
              type="text"
              className={styles.inputStyles}
              onChange={(e) => {
                setUserData({
                  ...userData,
                  company: e.target.value,
                });
              }}
              value={userData.company}
              name="company"
            />
          </div>
          <div>
            <label>CNPJ</label>
            <input
              type="text"
              className={styles.inputStyles}
              onChange={(e) => {
                setUserData({
                  ...userData,
                  cnpj: e.target.value,
                });
              }}
              value={userData.cnpj}
              name="cnpj"
            />
          </div>
        </form>
      </div>
      <div className={styles.containerRemove}>
        <div className={styles.quadradoAzul}>
          <img
            className={styles.logoRemove}
            src={logoReset}
            alt="ImagLogoRemove"
          />
        </div>
        <button
          className={styles.linkRetangular}
          onClick={() => setOpenModal(true)}
        >
          RESETAR SENHA
        </button>
      </div>
      <div className={styles.containerAdd}>
        <div className={styles.quadradoAzul}>
          <img className={styles.logoCad} src={logoSave} alt="ImagLogoAddem" />
        </div>
        <button className={styles.linkRetangular} onClick={handleSaveChanges}>
          SALVAR ALTERAÇÕES
        </button>
      </div>
      <ResetPopUp
        open={openModal}
        onClose={() => {
          setOpenModal(true);
        }}
        setOpenModal={setOpenModal}
      />
    </div>
  );
};

export default EditUser;
