import { useState } from "react";
import styles from "./DeletePopUp.module.css";

const DeletePopUp = ({ open, onClose }) => {
  const [selectedUsers, setSelectedUsers] = useState([]);

  const handleCheckboxChange = (userId) => {
    if (selectedUsers.includes(userId)) {
      setSelectedUsers(selectedUsers.filter((id) => id !== userId));
    } else {
      setSelectedUsers([...selectedUsers, userId]);
    }
  };

  if (!open) return null;
  return (
    <div onClick={onClose} className={styles.overlay}>
      <div
        onClick={(e) => {
          e.stopPropagation();
        }}
        className={styles.modalContainer}
      >
        <div className={styles.allContainer}>
          <div className={styles.content}>
            <p>Você está prestes a excluir os seguintes usuários:</p>
            {/*logica para exibir os usuarios que irão ser deletados*/}
            <p>Deseja prosseguir?</p>
          </div>
          <div className={styles.btnContainer}>
            <button className={styles.btnPrimary}>SIM</button>
            <button className={styles.btnOutline}>NÃO</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DeletePopUp;
