import styles from "./EditUser.module.css";
import { Link, Navigate, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import Navbar from "../../components/Navbar/Navbar";
import logoAdd from "../../assets/icons/edit_black_24dp.svg";
import logoReset from "../../assets/icons/baseline-refresh-24px.svg";
import logoSave from "../../assets/icons/baseline-save-24px.svg";

const EditUser = () => {
  const { id } = useParams();
  const [usuario, setUsuario] = useState("");
  const [nome, setNome] = useState("");
  const [empresa, setEmpresa] = useState("");
  const [cnpj, setCnpj] = useState("");

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await fetch(`http://localhost:3000/edituser/${id}`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        });

        if (!response.ok) {
          throw new Error("Erro ao buscar detalhes do usuário");
        }

        const userData = await response.json();
        setUsuario(userData.usuario);
        setNome(userData.nome);
        setEmpresa(userData.empresa);
        setCnpj(userData.cnpj);
      } catch (error) {
        console.error("Erro ao buscar detalhes do usuário:", error.message);
      }
    };

    fetchUserDetails();
  }, [id]);

  return (
    <div className={styles.container}>
      <Navbar />
      <div className={styles.infoRectangle}>
        <h1 className={styles.titleAdd}>
          <img src={logoAdd} alt="logoAdd" className={styles.logoAdd} />
          EDITAR USUÁRIO
        </h1>
        <div className={styles.containerInput}>
          <div>
            <label>USUÁRIO</label>
            <input
              type="text"
              className={styles.inputStyles}
              onChange={usuario}
            />
          </div>
          <div>
            <label>NOME</label>
            <input type="text" className={styles.inputStyles} onChange={nome} />
          </div>
          <div>
            <label>EMPRESA</label>
            <input
              type="text"
              className={styles.inputStyles}
              onChange={empresa}
            />
          </div>
          <div>
            <label>CNPJ</label>
            <input type="text" className={styles.inputStyles} onChange={cnpj} />
          </div>
        </div>
      </div>
      <div className={styles.containerRemove}>
        <div className={styles.quadradoCinza}>
          <img
            className={styles.logoRemove}
            src={logoReset}
            alt="ImagLogoRemove"
          />
        </div>
        <Link className={styles.linkRetangularCinza}>RESETAR SENHA</Link>
      </div>
      <div className={styles.containerAdd}>
        <div className={styles.quadradoAzul}>
          <img className={styles.logoCad} src={logoSave} alt="ImagLogoAddem" />
        </div>
        <Link className={styles.linkRetangular}>SALVAR ALTERAÇÕES</Link>
      </div>
    </div>
  );
};

export default EditUser;
